"use strict";function show_hide_password(t){const e=document.getElementById("password-input"),s=document.querySelector(".login__password-toggle");return"password"===e.getAttribute("type")?(t.classList.add("view"),e.setAttribute("type","text"),s.setAttribute("title","Скрыть пароль")):(t.classList.remove("view"),e.setAttribute("type","password"),s.setAttribute("title","Показать пароль")),!1}window.addEventListener("DOMContentLoaded",()=>{!function(){const t=document.querySelectorAll('.questionnaire__form_input[name="passwordToggle"]');document.querySelectorAll(".eyeToggle").forEach((e,s)=>{e.addEventListener("click",i=>{i.preventDefault(),"password"===t[s].getAttribute("type")?(e.classList.add("view"),t[s].setAttribute("type","text"),e.setAttribute("title","Скрыть пароль")):(e.classList.remove("view"),t[s].setAttribute("type","password"),e.setAttribute("title","Показать пароль"))})})}()});