 function show_hide_password(t){let e=document.getElementById("password-input"),s=document.querySelector(".login__password-toggle");return"password"===e.getAttribute("type")?(t.classList.add("view"),e.setAttribute("type","text"),s.setAttribute("title","Скрыть пароль")):(t.classList.remove("view"),e.setAttribute("type","password"),s.setAttribute("title","Показать пароль")),!1}
